package com.class3;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double d=2.5;
		double d1=4.7;
		
		
		
		if (d>d1) {
			System.out.println("Double value "+d+" is larger than "+d1);
			
		}else {
			System.out.println("Double value "+d+" is smaller than "+d1);
		}
		

	}

}
